schemas:
DEFINE TABLE OVERWRITE audit_log SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON audit_log TYPE option<record<user>>;
DEFINE FIELD OVERWRITE action ON audit_log TYPE string;
DEFINE FIELD OVERWRITE status ON audit_log TYPE string;
DEFINE FIELD OVERWRITE ip ON audit_log TYPE option<string>;
DEFINE FIELD OVERWRITE user_agent ON audit_log TYPE option<string>;
DEFINE FIELD OVERWRITE detail ON audit_log TYPE option<string>;
DEFINE FIELD OVERWRITE created_at ON audit_log TYPE datetime;

DEFINE INDEX OVERWRITE idx_audit_log_created_at ON audit_log FIELDS created_at;
DEFINE INDEX OVERWRITE idx_audit_log_with_user_id ON audit_log FIELDS user_id;
DEFINE INDEX OVERWRITE idx_audit_log_action ON audit_log FIELDS action;
DEFINE INDEX OVERWRITE idx_audit_log_user_action_time ON audit_log FIELDS user_id, action, created_at;
DEFINE INDEX OVERWRITE idx_audit_log_status ON audit_log FIELDS status;

DEFINE TABLE OVERWRITE cipher SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON cipher TYPE record<user>;
DEFINE FIELD OVERWRITE cipher_type ON cipher TYPE string;
DEFINE FIELD OVERWRITE folder ON cipher TYPE option<record<folder>>;
DEFINE FIELD OVERWRITE organization ON cipher TYPE option<record<organization>>;
DEFINE FIELD OVERWRITE data ON cipher TYPE string;
DEFINE FIELD OVERWRITE remarks ON cipher TYPE option<string>;
DEFINE FIELD OVERWRITE additional_fields ON cipher TYPE option<array<object>>;
DEFINE FIELD OVERWRITE created_at ON cipher TYPE datetime;
DEFINE FIELD OVERWRITE updated_at ON cipher TYPE datetime;

DEFINE TABLE OVERWRITE device SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON device TYPE record<user>;
DEFINE FIELD OVERWRITE device_name ON device TYPE string;
DEFINE FIELD OVERWRITE device_type ON device TYPE string;
DEFINE FIELD OVERWRITE raw_user_agent ON device TYPE string;
DEFINE FIELD OVERWRITE first_login_at ON device TYPE datetime;
DEFINE FIELD OVERWRITE last_login_at ON device TYPE datetime;
DEFINE FIELD OVERWRITE ip ON device TYPE string;
DEFINE FIELD OVERWRITE is_trusted ON device TYPE bool;

DEFINE INDEX OVERWRITE idx_device_with_user_id ON device FIELDS user_id;
DEFINE INDEX OVERWRITE idx_device_name_with_user_id ON device FIELDS user_id, device_name UNIQUE;
DEFINE INDEX OVERWRITE idx_device_type_with_user_id ON device FIELDS user_id, device_type;
DEFINE INDEX OVERWRITE idx_device_trusted_with_user_id ON device FIELDS user_id, is_trusted;
DEFINE INDEX OVERWRITE idx_device_user_last_login_at ON device FIELDS user_id, last_login_at;

DEFINE TABLE OVERWRITE folder SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON folder TYPE record<user>;
DEFINE FIELD OVERWRITE name ON folder TYPE string;

DEFINE TABLE OVERWRITE organization SCHEMAFULL;

DEFINE FIELD OVERWRITE name ON organization TYPE string;
DEFINE FIELD OVERWRITE members ON organization TYPE array<record<user>>;

DEFINE TABLE OVERWRITE refresh_token SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON refresh_token TYPE record<user>;
DEFINE FIELD OVERWRITE token_hashed ON refresh_token TYPE string;
DEFINE FIELD OVERWRITE device ON refresh_token TYPE record<device>;
DEFINE FIELD OVERWRITE ip ON refresh_token TYPE string;
DEFINE FIELD OVERWRITE created_at ON refresh_token TYPE datetime;
DEFINE FIELD OVERWRITE expires_at ON refresh_token TYPE datetime;
DEFINE FIELD OVERWRITE is_revoked ON refresh_token TYPE bool;

DEFINE INDEX OVERWRITE idx_refresh_token_token_hashed ON refresh_token FIELDS token_hashed UNIQUE;
DEFINE INDEX OVERWRITE idx_refresh_token_with_user_id ON refresh_token FIELDS user_id;
DEFINE INDEX OVERWRITE idx_refresh_token_with_device ON refresh_token FIELDS device;
DEFINE INDEX OVERWRITE idx_refresh_token_valid_token ON refresh_token FIELDS token_hashed, is_revoked, expires_at;
DEFINE INDEX OVERWRITE idx_refresh_token_expired_token ON refresh_token FIELDS expires_at;

DEFINE TABLE OVERWRITE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD OVERWRITE script_name ON script_migration TYPE string;
DEFINE FIELD OVERWRITE executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE FIELD OVERWRITE checksum ON script_migration TYPE option<string>;

DEFINE TABLE OVERWRITE user SCHEMAFULL;

DEFINE FIELD OVERWRITE name ON user TYPE string;
DEFINE FIELD OVERWRITE email ON user TYPE string;
DEFINE FIELD OVERWRITE password_hashed ON user TYPE string;
DEFINE FIELD OVERWRITE role ON user TYPE string;
DEFINE FIELD OVERWRITE is_verified ON user TYPE bool DEFAULT false;
DEFINE FIELD OVERWRITE is_banned ON user TYPE bool DEFAULT false;
DEFINE FIELD OVERWRITE created_at ON user TYPE datetime DEFAULT time::now() READONLY;
DEFINE FIELD OVERWRITE updated_at ON user TYPE datetime DEFAULT time::now();

DEFINE INDEX OVERWRITE idx_user_email ON user FIELDS email UNIQUE;
DEFINE INDEX OVERWRITE idx_user_role ON user FIELDS role;
DEFINE INDEX OVERWRITE idx_user_is_banned ON user FIELDS is_banned;
DEFINE INDEX OVERWRITE idx_user_created_at ON user FIELDS created_at;

DEFINE TABLE OVERWRITE vault SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON vault TYPE record<user>;
DEFINE FIELD OVERWRITE ciphers ON vault TYPE array<record<cipher>>;
DEFINE FIELD OVERWRITE folders ON vault TYPE array<record<folder>>;
DEFINE FIELD OVERWRITE organizations ON vault TYPE array<record<organization>>;
