schemas:
DEFINE TABLE OVERWRITE account SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON account TYPE record<user>;
DEFINE FIELD OVERWRITE project_name ON account TYPE string;
DEFINE FIELD OVERWRITE project_owner ON account TYPE record<user | group>;
DEFINE FIELD OVERWRITE folder ON account TYPE option<array<record<folder>>>;
DEFINE FIELD OVERWRITE username ON account TYPE option<string>;
DEFINE FIELD OVERWRITE password ON account TYPE option<string>;
DEFINE FIELD OVERWRITE web_url ON account TYPE option<string>;
DEFINE FIELD OVERWRITE remarks ON account TYPE option<string>;
DEFINE FIELD OVERWRITE additional_fields ON account TYPE option<array<object>>;
DEFINE FIELD OVERWRITE created_at ON account TYPE datetime;
DEFINE FIELD OVERWRITE updated_at ON account TYPE datetime;

DEFINE TABLE OVERWRITE audit_log SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON audit_log TYPE option<record<user>>;
DEFINE FIELD OVERWRITE action ON audit_log TYPE string;
DEFINE FIELD OVERWRITE status ON audit_log TYPE string;
DEFINE FIELD OVERWRITE ip ON audit_log TYPE option<string>;
DEFINE FIELD OVERWRITE user_agent ON audit_log TYPE option<string>;
DEFINE FIELD OVERWRITE detail ON audit_log TYPE option<string>;
DEFINE FIELD OVERWRITE created_at ON audit_log TYPE datetime;

DEFINE INDEX OVERWRITE idx_audit_log_created_at ON audit_log FIELDS created_at;
DEFINE INDEX OVERWRITE idx_audit_log_with_user_id ON audit_log FIELDS user_id;
DEFINE INDEX OVERWRITE idx_audit_log_action ON audit_log FIELDS action;
DEFINE INDEX OVERWRITE idx_audit_log_user_action_time ON audit_log FIELDS user_id, action, created_at;
DEFINE INDEX OVERWRITE idx_audit_log_status ON audit_log FIELDS status;

DEFINE TABLE OVERWRITE card SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON card TYPE record<user>;
DEFINE FIELD OVERWRITE project_name ON card TYPE string;
DEFINE FIELD OVERWRITE project_owner ON card TYPE record<user | group>;
DEFINE FIELD OVERWRITE folder ON card TYPE option<array<record<folder>>>;
DEFINE FIELD OVERWRITE owner_name ON card TYPE option<string>;
DEFINE FIELD OVERWRITE card_number ON card TYPE option<string>;
DEFINE FIELD OVERWRITE card_brand ON card TYPE option<string>;
DEFINE FIELD OVERWRITE expired_month ON card TYPE option<array<string>>;
DEFINE FIELD OVERWRITE expired_year ON card TYPE option<number>;
DEFINE FIELD OVERWRITE safe_code ON card TYPE option<string>;
DEFINE FIELD OVERWRITE remarks ON card TYPE option<string>;
DEFINE FIELD OVERWRITE additional_fields ON card TYPE option<array<object>>;
DEFINE FIELD OVERWRITE created_at ON card TYPE datetime;
DEFINE FIELD OVERWRITE updated_at ON card TYPE datetime;

DEFINE TABLE OVERWRITE device SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON device TYPE record<user>;
DEFINE FIELD OVERWRITE device_name ON device TYPE string;
DEFINE FIELD OVERWRITE device_type ON device TYPE string;
DEFINE FIELD OVERWRITE raw_user_agent ON device TYPE string;
DEFINE FIELD OVERWRITE first_login_at ON device TYPE datetime;
DEFINE FIELD OVERWRITE last_login_at ON device TYPE datetime;
DEFINE FIELD OVERWRITE ip ON device TYPE string;
DEFINE FIELD OVERWRITE is_trusted ON device TYPE bool;

DEFINE INDEX OVERWRITE idx_device_with_user_id ON device FIELDS user_id;
DEFINE INDEX OVERWRITE idx_device_name_with_user_id ON device FIELDS user_id, device_name UNIQUE;
DEFINE INDEX OVERWRITE idx_device_type_with_user_id ON device FIELDS user_id, device_type;
DEFINE INDEX OVERWRITE idx_device_trusted_with_user_id ON device FIELDS user_id, is_trusted;
DEFINE INDEX OVERWRITE idx_device_user_last_login_at ON device FIELDS user_id, last_login_at;

DEFINE TABLE OVERWRITE folder SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON folder TYPE record<user>;
DEFINE FIELD OVERWRITE name ON folder TYPE string;
DEFINE FIELD OVERWRITE created_at ON folder TYPE datetime;
DEFINE FIELD OVERWRITE updated_at ON folder TYPE datetime;

DEFINE TABLE OVERWRITE group SCHEMAFULL;

DEFINE FIELD OVERWRITE name ON group TYPE string;
DEFINE FIELD OVERWRITE users ON group TYPE array<record<user>>;
DEFINE FIELD OVERWRITE created_at ON group TYPE datetime;
DEFINE FIELD OVERWRITE updated_at ON group TYPE datetime;

DEFINE TABLE OVERWRITE id SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON id TYPE record<user>;
DEFINE FIELD OVERWRITE project_name ON id TYPE string;
DEFINE FIELD OVERWRITE project_owner ON id TYPE record<user | group>;
DEFINE FIELD OVERWRITE folder ON id TYPE option<array<record<folder>>>;
DEFINE FIELD OVERWRITE status ON id TYPE string;
DEFINE FIELD OVERWRITE first_name ON id TYPE option<string>;
DEFINE FIELD OVERWRITE middle_name ON id TYPE option<string>;
DEFINE FIELD OVERWRITE last_name ON id TYPE option<string>;
DEFINE FIELD OVERWRITE username ON id TYPE option<string>;
DEFINE FIELD OVERWRITE company ON id TYPE option<string>;
DEFINE FIELD OVERWRITE social_security_number ON id TYPE option<string>;
DEFINE FIELD OVERWRITE passport_number ON id TYPE option<string>;
DEFINE FIELD OVERWRITE license_number ON id TYPE option<string>;
DEFINE FIELD OVERWRITE email ON id TYPE option<string>;
DEFINE FIELD OVERWRITE address1 ON id TYPE option<string>;
DEFINE FIELD OVERWRITE address2 ON id TYPE option<string>;
DEFINE FIELD OVERWRITE address3 ON id TYPE option<string>;
DEFINE FIELD OVERWRITE city ON id TYPE option<string>;
DEFINE FIELD OVERWRITE province ON id TYPE option<string>;
DEFINE FIELD OVERWRITE post_code ON id TYPE option<string>;
DEFINE FIELD OVERWRITE country ON id TYPE option<string>;
DEFINE FIELD OVERWRITE additional_fields ON id TYPE option<array<object>>;
DEFINE FIELD OVERWRITE created_at ON id TYPE datetime;
DEFINE FIELD OVERWRITE updated_at ON id TYPE datetime;

DEFINE TABLE OVERWRITE note SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON note TYPE record<user>;
DEFINE FIELD OVERWRITE project_name ON note TYPE string;
DEFINE FIELD OVERWRITE project_owner ON note TYPE record<user | group>;
DEFINE FIELD OVERWRITE folder ON note TYPE option<array<record<folder>>>;
DEFINE FIELD OVERWRITE remarks ON note TYPE option<string>;
DEFINE FIELD OVERWRITE additional_fields ON note TYPE option<array<object>>;
DEFINE FIELD OVERWRITE created_at ON note TYPE datetime;
DEFINE FIELD OVERWRITE updated_at ON note TYPE datetime;

DEFINE TABLE OVERWRITE refresh_token SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON refresh_token TYPE record<user>;
DEFINE FIELD OVERWRITE token_hashed ON refresh_token TYPE string;
DEFINE FIELD OVERWRITE device ON refresh_token TYPE record<device>;
DEFINE FIELD OVERWRITE ip ON refresh_token TYPE string;
DEFINE FIELD OVERWRITE created_at ON refresh_token TYPE datetime;
DEFINE FIELD OVERWRITE expires_at ON refresh_token TYPE datetime;
DEFINE FIELD OVERWRITE is_revoked ON refresh_token TYPE bool;

DEFINE INDEX OVERWRITE idx_refresh_token_token_hashed ON refresh_token FIELDS token_hashed UNIQUE;
DEFINE INDEX OVERWRITE idx_refresh_token_with_user_id ON refresh_token FIELDS user_id;
DEFINE INDEX OVERWRITE idx_refresh_token_with_device ON refresh_token FIELDS device;
DEFINE INDEX OVERWRITE idx_refresh_token_valid_token ON refresh_token FIELDS token_hashed, is_revoked, expires_at;
DEFINE INDEX OVERWRITE idx_refresh_token_expired_token ON refresh_token FIELDS expires_at;

DEFINE TABLE OVERWRITE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD OVERWRITE script_name ON script_migration TYPE string;
DEFINE FIELD OVERWRITE executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE FIELD OVERWRITE checksum ON script_migration TYPE option<string>;

DEFINE TABLE OVERWRITE ssh SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON ssh TYPE record<user>;
DEFINE FIELD OVERWRITE project_name ON ssh TYPE string;
DEFINE FIELD OVERWRITE project_owner ON ssh TYPE record<user | group>;
DEFINE FIELD OVERWRITE folder ON ssh TYPE option<array<record<folder>>>;
DEFINE FIELD OVERWRITE secret_key ON ssh TYPE option<string>;
DEFINE FIELD OVERWRITE public_key ON ssh TYPE option<string>;
DEFINE FIELD OVERWRITE fingerprint ON ssh TYPE option<string>;
DEFINE FIELD OVERWRITE remarks ON ssh TYPE option<string>;
DEFINE FIELD OVERWRITE addtional_fields ON ssh TYPE option<array<object>>;
DEFINE FIELD OVERWRITE created_at ON ssh TYPE datetime;
DEFINE FIELD OVERWRITE updated_at ON ssh TYPE datetime;

DEFINE TABLE OVERWRITE user SCHEMAFULL;

DEFINE FIELD OVERWRITE name ON user TYPE string;
DEFINE FIELD OVERWRITE email ON user TYPE string;
DEFINE FIELD OVERWRITE password_hashed ON user TYPE string;
DEFINE FIELD OVERWRITE role ON user TYPE string;
DEFINE FIELD OVERWRITE is_verified ON user TYPE bool DEFAULT false;
DEFINE FIELD OVERWRITE is_banned ON user TYPE bool DEFAULT false;
DEFINE FIELD OVERWRITE created_at ON user TYPE datetime DEFAULT time::now() READONLY;
DEFINE FIELD OVERWRITE updated_at ON user TYPE datetime DEFAULT time::now();

DEFINE INDEX OVERWRITE idx_user_email ON user FIELDS email UNIQUE;
DEFINE INDEX OVERWRITE idx_user_role ON user FIELDS role;
DEFINE INDEX OVERWRITE idx_user_is_banned ON user FIELDS is_banned;
DEFINE INDEX OVERWRITE idx_user_created_at ON user FIELDS created_at;

DEFINE TABLE OVERWRITE vault SCHEMAFULL;

DEFINE FIELD OVERWRITE user_id ON vault TYPE record<user>;
DEFINE FIELD OVERWRITE account_projects ON vault TYPE option<array<record<account>>>;
DEFINE FIELD OVERWRITE card_projects ON vault TYPE option<array<record<card>>>;
DEFINE FIELD OVERWRITE id_projects ON vault TYPE option<array<record<id>>>;
DEFINE FIELD OVERWRITE note_projects ON vault TYPE option<array<record<note>>>;
DEFINE FIELD OVERWRITE ssh_projects ON vault TYPE option<array<record<ssh>>>;
